        await axios.get('https://638f29464ddca317d7f026d5.mockapi.io/users').then(res=>{
            console.log(res.data);
            setAllData(res.data)
        })
    }
    React.useEffect(()=>{
        getAllData()
    },[])
  return (
   allData.map((res,i)=>{
    return(
      <>
