import React from 'react'
import './App.css'


function Todo() {
    const [todo, setAddTodo] = React.useState('');
    const [todos, setTodos] = React.useState([]);
    const [isAdd, setIsAdd] = React.useState(true);
    const [id, setId] = React.useState(null);

    const getTodo = (e) => {
        e.preventDefault();
        if (todo === "") {
            alert("Please Update the Value")
        }
        else {
            let todoObj = {
                id: Math.random(),
                todo: todo,
                date: Date.now(),


            }
            setTodos([todoObj, ...todos]);
            todos.unshift(todoObj);
            setAddTodo('')
        }
    }

    // const [editText, setEditText] = React.useState([]);
    // const editTodo=(e,i, todo)=>{
    //     document.getElementById("hello").value = todo;
    //     add = false;
    //     console.log(i)
    // }

    const updateTodo=(e,i)=>{
        e.preventDefault();
        var new_todo = todo;
        console.log("updating");
        console.log(id);
        if (todo === "") {
            alert("Please Update the Value")
        }
        else {
            setTodos(prevState=>{
                const newState = prevState.map(obj=>{
                    if(res.id == 1){
                        return{...res, todo: 'change vayo'};
                    }
                })
            })
            // setTodos(current => current.map(res=>{
            //     if (res.id == id){
            //         return{...obj, todo:new_todo}
            //     }

            // }))
        }
    }

    const deleteTodo=(e,id)=>{
        // setTodos(current => current.filter(obj=>{
        //     return obj.id !== id;

        // }))
    }

    return (
        <>
            <div className="container">
                <div>
                    <form className="todoForm">
                        {/* <input type="text" placeholder='Add your ToDo here.' onChange={(e) => setAddTodo({ ...addTodo, text:e.target.value })} /> */}
                        <input type="text" placeholder='Add your ToDo here.' onChange={(e) => { setAddTodo(e.target.value) }} 
                        value={todo} />
                        <button className='addTodo' onClick={(e) => isAdd ? getTodo(e): updateTodo(e)} >{isAdd ? 'Add ToDo' : 'Edit'}</button>
                    </form>
                </div>
                <div className="todoItems">
                    {
                        todos.map((res,id) => {
                            return (
                                <div key={res.id} className="todoItem">
                                    <div className="todoText">
                                        <p>{res.todo}</p>
                                    </div>
                                    <div className="todoAction">
                                        <button className="btnEdit" 
                                        onClick={(e, todo) => {
                                            // editTodo(e,id,res.todo)
                                            setAddTodo(res.todo);
                                            setIsAdd(false);
                                            setId(id);
                                        }
                                        }>Edit</button>
                                        {/* <button className="btnEdit" onClick={(e)=>setAddTodo(todos[index]['todo'])}>Edit</button> */}
                                        <button className="btnDelete" onClick={()=>{setTodos(
                                            res.filter(a => a.id !== todo.id)
                                        )}}>Delete</button>
                                    </div>
                                </div>
                            )
                        })
                    }
                </div>
            </div>
        </>
    );
}

export default Todo;
